import React from "https://esm.sh/react@18.2.0";
import ReactDOM from "https://esm.sh/react-dom@18.2.0";

function parseData(obj) {
  if (typeof obj === 'string') {
    try {
      const parsed = JSON.parse(obj);
      if (parsed.data && typeof parsed.data === 'string') {
        return JSON.parse(parsed.data);
      }
      return parsed;
    } catch {
      return { error: "Errore di parsing JSON" }
    }
  } else if (obj && typeof obj.data === 'string') {
    try {
      return JSON.parse(obj.data);
    } catch {
      return { error: "Errore di parsing JSON" }
    }
  }
  return obj;
}

function StarRating({ value = 0, onChange, isRequired = false, hasError = false }) {
  const [hoveredStar, setHoveredStar] = React.useState(0);

  return (
    <div 
      className={`star-rating ${hasError ? 'error' : ''}`}
      onMouseLeave={() => setHoveredStar(0)}
    >
      {[1, 2, 3, 4, 5].map((star) => {
        const isHighlighted = hoveredStar > 0 ? star <= hoveredStar : star <= value;
        const isHovered = hoveredStar > 0 && star === hoveredStar;

        return (
          <span
            key={star}
            className={`star ${!isHighlighted ? 'inactive' : ''} ${isHovered ? 'hovered' : ''}`}
            onClick={() => onChange(star)}
            onMouseEnter={() => setHoveredStar(star)}
            title={`Valuta ${star}/5`}
          >
            ★
          </span>
        );
      })}
      {isRequired && <span className="required-indicator">*</span>}
      {hasError && <span className="error-text">Campo obbligatorio</span>}
    </div>
  );
}

function HeaderInfo({ data }) {
  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('it-IT', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric'
      });
    } catch {
      return 'N/A';
    }
  };

  const nome = data.Nome || 'N/A';
  const neg = data.Neg || 'N/A';
  const dataAssunzione = data.DataAssunzione || null;
  const dataFineContratto = data.DataFineContratto || null;

  const detectCategory = (data) => {
    for (let i = 1; i <= 4; i++) {
      const hasDataForCategory = Object.keys(data).some(key => key.startsWith(i.toString()));
      if (hasDataForCategory) {
        return i;
      }
    }
    return 1;
  };

  const currentCategory = detectCategory(data);

  return (
    <div className="header-info">
      <h2 className="header-title">
       <span className="highlight">{currentCategory}°</span>Settimana di <span className="highlight">{nome}</span> assunta su <span className="highlight">{neg}</span> il <span className="highlight">{formatDate(dataAssunzione)}</span> con scadenza al <span className="highlight">{formatDate(dataFineContratto)}</span>
      </h2>
    </div>
  );
}

function DetailsTable({ fields, onFieldChange, editingNotes, onNoteFocus, onNoteChange, onNoteBlur, validationErrors }) {
  // Funzione per determinare se un campo è obbligatorio
  function isRequiredField(key) {
    // I campi di valutazione (non note) sono obbligatori
    const scoreMatch = key.match(/^[1234](?!.*Note).*/i);
    return !!scoreMatch;
  }

  function renderInput(key, value) {
    const noteMatch = key.match(/^[1234].*Note.*/i);
    const scoreMatch = key.match(/^[1234](?!.*Note).*/i);
    const isRequired = isRequiredField(key);
    const hasError = validationErrors.includes(key);

    if (noteMatch) {
      return (
        <textarea
          value={key in editingNotes ? editingNotes[key] : value ?? ''}
          onFocus={() => onNoteFocus(key)}
          onChange={e => onNoteChange(key, e.target.value)}
          onBlur={() => onNoteBlur(key)}
          rows={2}
          placeholder="Scrivi una nota..."
          autoFocus={false}
          className={hasError ? 'error' : ''}
        />
      );
    } else if (scoreMatch) {
      return (
        <StarRating
          value={parseInt(value) || 0}
          onChange={val => onFieldChange(key, val)}
          isRequired={isRequired}
          hasError={hasError}
        />
      );
    } else {
      return <span>{String(value ?? "")}</span>;
    }
  }

  return (
    <table className="detail-table">
      <tbody>
        {Object.entries(fields).map(([key, value]) => {
          const isRequired = isRequiredField(key);
          const hasError = validationErrors.includes(key);

          return (
            <tr key={key} className={hasError ? 'error-row' : ''}>
              <th>
                {key}
                {isRequired && <span className="required-indicator">*</span>}
              </th>
              <td>{renderInput(key, value)}</td>
            </tr>
          );
        })}
      </tbody>
    </table>
  );
}

function App() {
  const [data, setData] = React.useState(() => parseData(appsmith.model.data));
  const [edited, setEdited] = React.useState({});
  const [editingNotes, setEditingNotes] = React.useState({});
  const [isSubmitting, setIsSubmitting] = React.useState(false);
  const [validationErrors, setValidationErrors] = React.useState([]);

  const containerRef = React.useRef(null);

  React.useEffect(() => {
    const parsedData = parseData(appsmith.model.data);
    setData(parsedData);
    setEdited({});
    setEditingNotes({});
    setValidationErrors([]);

    setTimeout(() => {
      if (containerRef.current) {
        containerRef.current.scrollTop = 0;
      }
      document.documentElement.scrollTop = 0;
      document.body.scrollTop = 0;
      window.scrollTo(0, 0);
    }, 10);
  }, [appsmith.model.data]);

  React.useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollTop = 0;
    }
    document.documentElement.scrollTop = 0;
    document.body.scrollTop = 0;
    window.scrollTo(0, 0);
  }, []);

  function updateField(key, value) {
    const newEdited = { ...edited, [key]: value };
    setEdited(newEdited);

    // Rimuovi il campo dagli errori se ora ha un valore valido
    if (value && validationErrors.includes(key)) {
      setValidationErrors(prev => prev.filter(error => error !== key));
    }

    appsmith.updateModel({ 
      data: JSON.stringify(data), 
      edited: JSON.stringify(newEdited),
      allData: { ...data, ...newEdited },
      isReady: true
    });
  }

  function handleNoteFocus(key) {
    setEditingNotes((old) => ({ 
      ...old, 
      [key]: edited[key] || data[key] || "" 
    }));
  }

  function handleNoteChange(key, value) {
    setEditingNotes((old) => ({ ...old, [key]: value }));
  }

  function handleNoteBlur(key) {
    updateField(key, editingNotes[key]);
    setEditingNotes((old) => {
      const obj = { ...old };
      delete obj[key];
      return obj;
    });
  }

  // Funzione di validazione
  function validateForm() {
    const finalData = { ...data, ...edited };
    const errors = [];

    // Trova tutti i campi di valutazione (non note) che devono essere compilati
    Object.keys(finalData).forEach(key => {
      const scoreMatch = key.match(/^[1234](?!.*Note).*/i);
      if (scoreMatch) {
        const value = finalData[key];
        if (!value || value === 0 || value === '0' || value === '') {
          errors.push(key);
        }
      }
    });

    return errors;
  }

  // Funzione per scrollare al primo errore
  function scrollToFirstError(errors) {
    if (errors.length > 0 && containerRef.current) {
      const firstErrorField = errors[0];
      const errorElement = containerRef.current.querySelector(`[data-field="${firstErrorField}"]`) ||
                          containerRef.current.querySelector('.error-row');

      if (errorElement) {
        errorElement.scrollIntoView({ 
          behavior: 'smooth', 
          block: 'center' 
        });
      }
    }
  }

  async function onInviaClick() {
    // Valida il form prima di inviare
    const errors = validateForm();

    if (errors.length > 0) {
      setValidationErrors(errors);

      // Mostra un messaggio di errore più specifico
      const firstError = errors[0];
      const errorMessage = `Attenzione! Devi compilare tutti i campi obbligatori. Il primo campo mancante è: "${firstError}"`;

      alert(errorMessage);

      // Scrolla al primo errore
      setTimeout(() => scrollToFirstError(errors), 100);

      return;
    }

    setIsSubmitting(true);
    setValidationErrors([]);

    try {
      const finalData = { ...data, ...edited };

      console.log("Dati finali da salvare:", finalData);

      appsmith.updateModel({ 
        data: JSON.stringify(data), 
        edited: JSON.stringify(edited),
        allData: finalData,
        isReady: true
      });

      await new Promise(resolve => setTimeout(resolve, 100));
      await appsmith.triggerEvent("onInviaClick");

    } catch (error) {
      console.error("Errore durante l'invio:", error);
      alert("Errore durante il salvataggio: " + error.message);
    } finally {
      setIsSubmitting(false);
    }
  }

  const displayValues = {};
  Object.keys(data).forEach(key => {
    displayValues[key] = edited.hasOwnProperty(key) ? edited[key] : data[key];
  });

  // Conta i campi obbligatori compilati per mostrare il progresso
  const requiredFields = Object.keys(data).filter(key => key.match(/^[1234](?!.*Note).*/i));
  const completedFields = requiredFields.filter(key => {
    const value = displayValues[key];
    return value && value !== 0 && value !== '0' && value !== '';
  });

  return (
    <div className="custom-table-wrapper" ref={containerRef}>
      {/* Header fisso in alto */}
      <HeaderInfo data={data} />

      {/* Indicatore di progresso */}
      <div className="progress-indicator">
        <span>Campi obbligatori completati: {completedFields.length}/{requiredFields.length}</span>
        {validationErrors.length > 0 && (
          <span className="validation-summary">
            ⚠️ {validationErrors.length} campo/i da completare
          </span>
        )}
      </div>

      {/* Contenitore della tabella con scroll */}
      <div className="table-container">
        <DetailsTable
          fields={displayValues}
          onFieldChange={updateField}
          editingNotes={editingNotes}
          onNoteFocus={handleNoteFocus}
          onNoteChange={handleNoteChange}
          onNoteBlur={handleNoteBlur}
          validationErrors={validationErrors}
        />
      </div>

      {/* Pulsante fisso in basso */}
      <button 
        className={`button-invia ${validationErrors.length > 0 ? 'error' : ''}`}
        onClick={onInviaClick} 
        type="button"
        disabled={isSubmitting}
      >
        {isSubmitting ? "Invio in corso..." : "Invia"}
      </button>
    </div>
  );
}

function renderApp() {
  ReactDOM.render(<App />, document.getElementById("root"));

  setTimeout(() => {
    document.documentElement.scrollTop = 0;
    document.body.scrollTop = 0;
    window.scrollTo(0, 0);
  }, 0);
}

appsmith.onReady(() => {
  document.documentElement.scrollTop = 0;
  document.body.scrollTop = 0;
  window.scrollTo(0, 0);
  renderApp();
});

appsmith.onModelChange(() => {
  renderApp();
});
