#root {
  height: 100%;
}

.custom-table-wrapper {
  background-color: var(--appsmith-theme-backgroundColor, #ffffff);
  padding: 16px;
  border-radius: var(--appsmith-theme-borderRadius);
  height: 100%;
  overflow-y: auto;
  font-family: inherit;
  color: var(--appsmith-theme-textColor, #333);
}

.detail-table {
  width: 100%;
  border-collapse: collapse;
  table-layout: fixed;
}

.detail-table th, .detail-table td {
  border: 1px solid #E0E0E0;
  padding: 10px;
  text-align: left;
  vertical-align: middle;
  word-wrap: break-word;
}

.detail-table th {
  background-color: #FAFAFA;
  font-weight: 600;
  width: 30%;
}

.star-rating {
  direction: ltr;
  unicode-bidi: bidi-override;
}

.star {
  display: inline-block;
  cursor: pointer;
  font-size: 1.7em;
  color: #ffd700;
  transition: transform 0.1s;
}

.star.inactive {
  color: #e0e0e0;
}

input[type=text], textarea {
  font-size: 1em;
  border: 1px solid #d0d0d0;
  background: #fff;
  border-radius: 4px;
  width: 100%;
  padding: 6px 8px;
  resize: vertical;
  box-sizing: border-box;
}

.button-invia {
  display: block;
  margin: 20px auto 0 auto;
  background: var(--appsmith-theme-primaryColor, #754ffe);
  color: #fff;
  border: none;
  border-radius: 5px;
  padding: 12px 28px;
  font-weight: bold;
  font-size: 1.1em;
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.04);
  cursor: pointer;
  transition: background 0.2s;
}

.button-invia:hover {
  background: var(--appsmith-theme-primaryColorHover, #593cbe);
}

